svg.script:
    // 
      const passwordInput = document.getElementById('pwd');
      const strengthBar = document.getElementById('strength-bar');
      const strengthText = document.getElementById('strength-text');

      const strengthLevels = {
        0: { text: 'Strength: (none)', color: '#e0e0e0', width: 0 },
        1: { text: 'Strength: Very Weak', color: '#e74c3c', width: 0.2 },
        2: { text: 'Strength: Weak', color: '#f1c40f', width: 0.4 },
        3: { text: 'Strength: Medium', color: '#2ecc71', width: 0.6 },
        4: { text: 'Strength: Strong', color: '#27ae60', width: 0.8 },
        5: { text: 'Strength: Very Strong', color: '#16a085', width: 1.0 }
      };

      passwordInput.addEventListener('input', () => {
        const password = passwordInput.value;
        let score = 0;

        if (password.length > 0) {
            score = 1; // Base score for having a password
            if (password.length >= 8) score++;
            if (/[A-Z]/.test(password)) score++;
            if (/[0-9]/.test(password)) score++;
            if (/[^A-Za-z0-9]/.test(password)) score++;
        }

        const level = strengthLevels[score];
        const barWidth = 330 * level.width; // 330 is the full width of the bar

        // Update SVG attributes
        strengthBar.setAttribute('width', barWidth);
        strengthBar.setAttribute('fill', level.color);

        // Update text content
        strengthText.textContent = level.text;
      });
    //
